      <div class="EcommerceMedal col-sm-12 col-md-12 col-xl-6 p-2">
        <emailsendVue @newEmail="newemailCon" @editEmail="editEmail" />
      </div>
      <div class="EcommerceMedal col-sm-12 col-md-12 col-xl-6 p-2">
        <exampelVue @data="data_email" @delet="delet_row" />
      </div>
    </div>
    <input type="text" v-model="textModel">
  </div>
</template>

