<script lang="ts">
import type {Booleanish, LinkTarget} from '../../types'
import {useBooleanish} from '../../composables'
import {computed, defineComponent, getCurrentInstance, type PropType, ref, toRef} from 'vue'
import type {RouteLocation, RouteLocationRaw} from 'vue-router'

export const BLINK_PROPS = {
  active: {type: [Boolean, String, undefined] as PropType<Booleanish>, default: undefined},
  activeClass: {type: String, default: 'router-link-active'},
  append: {type: [Boolean, String] as PropType<Booleanish>, default: false},
  disabled: {type: [Boolean, String] as PropType<Booleanish>, default: false},
  event: {type: [String, Array], default: 'click'},
  href: {type: String},
  // noPrefetch: {type: [Boolean, String] as PropType<Booleanish>, default: false},
  // prefetch: {type: [Boolean, String] as PropType<Booleanish>, default: null},
  rel: {type: String, default: null},
  replace: {type: [Boolean, String] as PropType<Booleanish>, default: false},
  routerComponentName: {type: String, default: 'router-link'},
  routerTag: {type: String, default: 'a'},
  target: {type: String as PropType<LinkTarget>, default: '_self'},
  to: {type: [String, Object] as PropType<RouteLocationRaw>, default: null},
}

export default defineComponent({
  // TODO this component will likely have an issue with inheritAttrs
  props: BLINK_PROPS,
  emits: ['click'],
  setup(props, {emit, attrs}) {
    const activeBoolean = useBooleanish(toRef(props, 'active'))
    const appendBoolean = useBooleanish(toRef(props, 'append'))
    const disabledBoolean = useBooleanish(toRef(props, 'disabled'))
    const replaceBoolean = useBooleanish(toRef(props, 'replace'))

    const instance = getCurrentInstance()
    const link = ref<HTMLElement>(null as unknown as HTMLElement)

    const tag = computed<string>(() => {
      const routerName = props.routerComponentName
        .split('-')
        .map((e) => e.charAt(0).toUpperCase() + e.slice(1))
        .join('')
      const hasRouter = instance?.appContext.app.component(routerName) !== undefined
      if (!hasRouter || disabledBoolean.value || !props.to) {
        return 'a'
      }
      return props.routerComponentName
    })

    const computedHref = computed<string>(() => {
      const toFallback = '#'
      if (props.href) return props.href

      if (typeof props.to === 'string') return props.to || toFallback

      const to = props.to as RouteLocation

      if (
        Object.prototype.toString.call(to) === '[object Object]' &&
        (to.path || to.query || to.hash)
      ) {
        const path = to.path || ''
        const query = to.query
          ? `?${Object.keys(to.query)
              .map((e) => `${e}=${to.query[e]}`)
              .join('=')}`
          : ''
        const hash = !to.hash || to.hash.charAt(0) === '#' ? to.hash || '' : `#${to.hash}`
        return `${path}${query}${hash}` || toFallback
      }

      return toFallback
    })

    const routerAttr = computed(() => ({
      'to': props.to,
      'href': computedHref.value,
      'target': props.target,
      'rel': props.target === '_blank' && props.rel === null ? 'noopener' : props.rel || null,
      'tabindex': disabledBoolean.value
        ? '-1'
        : typeof attrs.tabindex === 'undefined'
        ? null
        : attrs.tabindex,
      'aria-disabled': disabledBoolean.value ? true : null,
    }))

    const computedLinkClasses = computed(() => ({
      active: activeBoolean.value,
      disabled: disabledBoolean.value,
    }))

    const clicked = (e: MouseEvent): void => {
      if (disabledBoolean.value) {
        e.preventDefault()
        e.stopImmediatePropagation()
        return
      }
      emit('click', e)
    }

    return {
      computedLinkClasses,
      tag,
      routerAttr,
      link,
      clicked,
      activeBoolean,
      appendBoolean,
      disabledBoolean,
      replaceBoolean,
    }
  },
})
</script>
<template>
  <component
    :is="computedTag"
    class="btn"
    :class="computedClasses"
    v-bind="computedAttrs"
    @click="clicked"
  >
    <div
      v-if="loadingBoolean"
      class="btn-loading"
      :class="{'mode-fill': loadingMode === 'fill', 'mode-inline': loadingMode === 'inline'}"
    >
      <slot name="loading">
        <b-spinner class="btn-spinner" :small="size !== 'lg'" />
      </slot>
    </div>
    <div
      class="btn-content"
      :class="{'btn-loading-fill': loadingBoolean && loadingMode === 'fill'}"
    >
      <slot />
    </div>
  </component>
</template>

<script lang="ts">
import {computed, defineComponent, type PropType, toRef} from 'vue'
import BSpinner from '../BSpinner.vue'
import {useBooleanish} from '../../composables'
import type {Booleanish, ButtonType, ButtonVariant, InputSize, LinkTarget} from '../../types'
import {isLink} from '../../utils'
import BLink, {BLINK_PROPS} from '../BLink/BLink.vue'
import {useVModel} from '@vueuse/core'

export default defineComponent({
  components: {BLink, BSpinner},
  props: {
    ...BLINK_PROPS,
    active: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    disabled: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    href: {type: String, default: undefined},
    pill: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    pressed: {type: [Boolean, String] as PropType<Booleanish>, default: null},
    rel: {type: String, default: undefined},
    size: {type: String as PropType<InputSize>, default: 'md'},
    squared: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    tag: {type: String, default: 'button'},
    target: {type: String as PropType<LinkTarget>, default: '_self'},
    type: {type: String as PropType<ButtonType>, default: 'button'},
    variant: {type: String as PropType<ButtonVariant>, default: 'secondary'},
    loading: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    loadingMode: {type: String as PropType<'fill' | 'inline'>, default: 'inline'},
  },
  emits: ['click', 'update:pressed'],
  setup(props, {emit}) {
    const pressedValue = useVModel(props, 'pressed', emit)

    const activeBoolean = useBooleanish(toRef(props, 'active'))
    const disabledBoolean = useBooleanish(toRef(props, 'disabled'))
    const pillBoolean = useBooleanish(toRef(props, 'pill'))
    const pressedBoolean = useBooleanish(toRef(props, 'pressed'))
    const squaredBoolean = useBooleanish(toRef(props, 'squared'))
    const loadingBoolean = useBooleanish(toRef(props, 'loading'))

    const isToggle = computed<boolean>(() => typeof pressedBoolean.value === 'boolean')
    const isButton = computed<boolean>(
      () => props.tag === 'button' && props.href === undefined && props.to === null
    )
    const computedLink = computed<boolean>(() => isLink(props))
    const isBLink = computed<boolean>(() => props.to !== null)
    const nonStandardTag = computed<boolean>(() =>
      props.href !== undefined ? false : !isButton.value
    )

    const computedClasses = computed(() => [
      [`btn-${props.variant}`],
      [`btn-${props.size}`],
      {
        'active': activeBoolean.value || pressedBoolean.value,
        'rounded-pill': pillBoolean.value,
        'rounded-0': squaredBoolean.value,
        'disabled': disabledBoolean.value,
      },
    ])

    const computedAttrs = computed(() => ({
      'aria-disabled': nonStandardTag.value ? disabledBoolean.value : null,
      'aria-pressed': isToggle.value ? pressedBoolean.value : null,
      'autocomplete': isToggle.value ? 'off' : null,
      'disabled': isButton.value ? disabledBoolean.value : null,
      'href': props.href,
      'rel': computedLink.value ? props.rel : null,
      'role': nonStandardTag.value || computedLink.value ? 'button' : null,
      'target': computedLink.value ? props.target : null,
      'type': isButton.value ? props.type : null,
      'to': !isButton.value ? props.to : null,
      'append': computedLink.value ? props.append : null,
      'activeClass': isBLink.value ? props.activeClass : null,
      'event': isBLink.value ? props.event : null,
      'replace': isBLink.value ? props.replace : null,
      'routerComponentName': isBLink.value ? props.routerComponentName : null,
      'routerTag': isBLink.value ? props.routerTag : null,
    }))

    const computedTag = computed<string | typeof BLink>(() =>
      isBLink.value ? BLink : props.href ? 'a' : props.tag
    )

    const clicked = (e: MouseEvent): void => {
      if (disabledBoolean.value) {
        e.preventDefault()
        e.stopPropagation()
        return
      }
      emit('click', e)
      if (isToggle.value) {
        pressedValue.value = !pressedBoolean.value
      }
    }

    return {
      computedClasses,
      computedAttrs,
      computedTag,
      clicked,
      loadingBoolean,
    }
  },
})
</script>
<template>
  <b-transition :no-fade="!fadeBoolean" :trans-props="{enterToClass: 'show'}">
    <div
      v-if="isAlertVisible"
      class="alert"
      role="alert"
      aria-live="polite"
      aria-atomic="true"
      :class="computedClasses"
      @mouseenter.stop="onMouseEnter"
      @mouseleave.stop="resume"
    >
      <slot />
      <template v-if="dismissibleBoolean">
        <!-- TODO this renders incorrectly -->
        <b-button v-if="hasCloseSlot || closeContent" v-bind="closeAttrs" @click="closeClicked">
          <slot name="close">
            {{ closeContent }}
          </slot>
        </b-button>
        <b-close-button
          v-else
          :aria-label="dismissLabel"
          v-bind="closeAttrs"
          @click="closeClicked"
        />
      </template>
    </div>
  </b-transition>
</template>

<script setup lang="ts">
import BTransition from '../BTransition/BTransition.vue'
import BCloseButton from '../BButton/BCloseButton.vue'
import BButton from '../BButton/BButton.vue'
import type {Booleanish, ButtonType, ButtonVariant, ColorVariant} from '../../types'
import {computed, onBeforeUnmount, toRef, useSlots, watchEffect} from 'vue'
import {useBooleanish, useCountdown} from '../../composables'
import {isEmptySlot} from '../../utils'
import {useVModel} from '@vueuse/core'

interface BAlertProps {
  noHoverPause?: Booleanish
  dismissLabel?: string
  dismissible?: Booleanish
  fade?: Booleanish
  closeVariant?: ButtonVariant
  modelValue?: boolean | number
  variant?: ColorVariant
  closeContent?: string
  immediate?: Booleanish
  interval?: number
  showOnPause?: Booleanish
}

const props = withDefaults(defineProps<BAlertProps>(), {
  closeContent: undefined,
  closeVariant: 'secondary',
  noHoverPause: false,
  interval: 1000,
  dismissLabel: 'Close',
  dismissible: false,
  fade: false,
  modelValue: false,
  variant: 'info',
  immediate: true,
  showOnPause: true,
})

interface BAlertEmits {
  (e: 'closed'): void
  (e: 'close-countdown', value: number): void
  (e: 'update:modelValue', value: boolean | number): void
}

const emit = defineEmits<BAlertEmits>()

const slots = useSlots()

const modelValue = useVModel(props, 'modelValue', emit)

const dismissibleBoolean = useBooleanish(toRef(props, 'dismissible'))
const fadeBoolean = useBooleanish(toRef(props, 'fade'))
const immediateBoolean = useBooleanish(toRef(props, 'immediate'))
const showOnPauseBoolean = useBooleanish(toRef(props, 'showOnPause'))
const noHoverPauseBoolean = useBooleanish(toRef(props, 'noHoverPause'))

const hasCloseSlot = computed<boolean>(() => !isEmptySlot(slots.close))

const countdownLength = computed(() =>
  typeof modelValue.value === 'boolean' ? 0 : modelValue.value
)

const computedClasses = computed(() => [
  [`alert-${props.variant}`],
  {
    'alert-dismissible': dismissibleBoolean.value,
  },
])

const {
  isActive,
  pause,
  restart,
  resume,
  stop,
  isPaused,
  value: remainingMs,
} = useCountdown(countdownLength, toRef(props, 'interval'), {
  immediate: typeof modelValue.value === 'number' && immediateBoolean.value,
})

const isAlertVisible = computed<boolean>(() =>
  typeof modelValue.value === 'boolean'
    ? modelValue.value
    : isActive.value || (showOnPauseBoolean.value && isPaused.value)
)

const closeAttrs = computed(() => ({
  variant: props.closeVariant,
  type: 'button' as ButtonType,
}))

watchEffect(() => emit('close-countdown', remainingMs.value))

const closeClicked = (): void => {
  if (typeof modelValue.value === 'boolean') {
    modelValue.value = false
  } else {
    modelValue.value = 0
    stop()
  }
  emit('closed')
}

// TODO mouseleave/mouseenter could be replaced with useElementHover with a watcher
const onMouseEnter = () => {
  if (noHoverPauseBoolean.value) return
  pause()
}

onBeforeUnmount(stop)

defineExpose({pause, resume, restart, stop})
</script>
<template>
  <component
    :is="computedTag"
    class="b-avatar"
    :class="computedClasses"
    :style="computedStyle"
    v-bind="computedAttrs"
    @click="clicked"
  >
    <span v-if="hasDefaultSlot" class="b-avatar-custom">
      <slot />
    </span>
    <span v-else-if="!!src" class="b-avatar-img">
      <img :src="src" :alt="alt" @error="onImgError" />
    </span>
    <span v-else-if="!!text" class="b-avatar-text" :class="textClasses" :style="textFontStyle">
      {{ text }}
    </span>
    <span v-if="showBadge" class="b-avatar-badge" :class="badgeClasses" :style="badgeStyle">
      <slot v-if="hasBadgeSlot" name="badge" />
      <span v-else :class="badgeTextClasses">{{ badgeText }}</span>
    </span>
  </component>
</template>

<script setup lang="ts">
import {avatarGroupInjectionKey, isEmptySlot, isNumeric, toFloat} from '../../utils'
import {computed, type CSSProperties, inject, type StyleValue, toRef, useSlots} from 'vue'
import type {Booleanish, ButtonType, ColorVariant, TextColorVariant} from '../../types'
import {useBooleanish} from '../../composables'

interface BAvatarProps {
  alt?: string
  ariaLabel?: string
  badge?: boolean | string
  badgeLeft?: Booleanish
  badgeOffset?: string
  badgeTop?: Booleanish
  badgeVariant?: ColorVariant
  button?: Booleanish
  buttonType?: ButtonType
  disabled?: Booleanish
  icon?: string
  rounded?: boolean | string
  size?: 'sm' | 'md' | 'lg' | string // InputSize | string
  square?: Booleanish
  src?: string
  text?: string
  textVariant?: TextColorVariant
  variant?: ColorVariant
}

const props = withDefaults(defineProps<BAvatarProps>(), {
  ariaLabel: undefined,
  badgeOffset: undefined,
  icon: undefined,
  size: undefined,
  src: undefined,
  text: undefined,
  textVariant: undefined,
  alt: 'avatar',
  badge: false,
  badgeLeft: false,
  badgeTop: false,
  badgeVariant: 'primary',
  button: false,
  buttonType: 'button',
  disabled: false,
  rounded: 'circle',
  square: false,
  variant: 'secondary',
})

interface BAvatarEmits {
  (e: 'click', value: MouseEvent): void
  (e: 'img-error', value: Event): void
}

const emit = defineEmits<BAvatarEmits>()

const slots = useSlots()

const parentData = inject(avatarGroupInjectionKey, null)

const SIZES = ['sm', null, 'lg']
const FONT_SIZE_SCALE = 0.4
const BADGE_FONT_SIZE_SCALE = FONT_SIZE_SCALE * 0.7

const badgeLeftBoolean = useBooleanish(toRef(props, 'badgeLeft'))
const badgeTopBoolean = useBooleanish(toRef(props, 'badgeTop'))
const buttonBoolean = useBooleanish(toRef(props, 'button'))
const disabledBoolean = useBooleanish(toRef(props, 'disabled'))
const squareBoolean = useBooleanish(toRef(props, 'square'))

const hasDefaultSlot = computed<boolean>(() => !isEmptySlot(slots.default))
const hasBadgeSlot = computed<boolean>(() => !isEmptySlot(slots.badge))

const showBadge = computed<boolean>(() => !!props.badge || props.badge === '' || hasBadgeSlot.value)

const computedSize = computed<string | null>(
  () => parentData?.size.value ?? computeSize(props.size)
)

const computedVariant = computed<ColorVariant>(() => parentData?.variant.value ?? props.variant)

const computedRounded = computed<string | boolean>(() => parentData?.rounded.value ?? props.rounded)

const computedAttrs = computed(() => ({
  'type': buttonBoolean.value ? props.buttonType : undefined,
  'aria-label': props.ariaLabel || null,
  'disabled': disabledBoolean.value || null,
}))

const badgeClasses = computed(() => [`bg-${props.badgeVariant}`])

const badgeText = computed<string | false>(() => (props.badge === true ? '' : props.badge))

const badgeTextClasses = computed(() => [[`text-${computeContrastVariant(props.badgeVariant)}`]])

const computedClasses = computed(() => ({
  [`b-avatar-${props.size}`]: !!props.size && SIZES.indexOf(computeSize(props.size)) !== -1,
  [`bg-${computedVariant.value}`]: !!computedVariant.value,
  [`badge`]: !buttonBoolean.value && computedVariant.value && hasDefaultSlot.value,
  rounded: computedRounded.value === '' || computedRounded.value === true,
  [`rounded-circle`]: !squareBoolean.value && computedRounded.value === 'circle',
  [`rounded-0`]: squareBoolean.value || computedRounded.value === '0',
  [`rounded-1`]: !squareBoolean.value && computedRounded.value === 'sm',
  [`rounded-3`]: !squareBoolean.value && computedRounded.value === 'lg',
  [`rounded-top`]: !squareBoolean.value && computedRounded.value === 'top',
  [`rounded-bottom`]: !squareBoolean.value && computedRounded.value === 'bottom',
  [`rounded-start`]: !squareBoolean.value && computedRounded.value === 'left',
  [`rounded-end`]: !squareBoolean.value && computedRounded.value === 'right',
  btn: buttonBoolean.value,
  [`btn-${computedVariant.value}`]: buttonBoolean.value ? !!computedVariant.value : false,
}))

const textClasses = computed(() => [
  [`text-${props.textVariant || computeContrastVariant(computedVariant.value)}`],
])

const badgeStyle = computed<StyleValue>(() => {
  const offset = props.badgeOffset || '0px'
  const fontSize =
    SIZES.indexOf(computedSize.value || null) === -1
      ? `calc(${computedSize.value} * ${BADGE_FONT_SIZE_SCALE})`
      : ''
  return {
    fontSize: fontSize || '',
    top: badgeTopBoolean.value ? offset : '',
    bottom: badgeTopBoolean.value ? '' : offset,
    left: badgeLeftBoolean.value ? offset : '',
    right: badgeLeftBoolean.value ? '' : offset,
  }
})

const textFontStyle = computed<StyleValue>(() => {
  const fontSize =
    SIZES.indexOf(computedSize.value || null) === -1
      ? `calc(${computedSize.value} * ${FONT_SIZE_SCALE})`
      : null
  return fontSize ? {fontSize} : {}
})

const marginStyle = computed(() => {
  const overlapScale = parentData?.overlapScale?.value || 0

  const value =
    computedSize.value && overlapScale ? `calc(${computedSize.value} * -${overlapScale})` : null
  return value ? {marginLeft: value, marginRight: value} : {}
})

const computedTag = computed<'button' | 'span'>(() => (buttonBoolean.value ? 'button' : 'span'))

const computedStyle = computed<CSSProperties>(() => ({
  ...marginStyle.value,
  width: computedSize.value ?? undefined,
  height: computedSize.value ?? undefined,
}))

const computeContrastVariant = (colorVariant: ColorVariant): 'dark' | 'light' =>
  colorVariant === 'light' || colorVariant === 'warning' ? 'dark' : 'light'

const clicked = (e: MouseEvent): void => {
  if (!disabledBoolean.value && buttonBoolean.value) emit('click', e)
}

const onImgError = (e: Event): void => emit('img-error', e)
</script>

<script lang="ts">
export const computeSize = (value: any): string | null => {
  const calcValue = typeof value === 'string' && isNumeric(value) ? toFloat(value, 0) : value
  return typeof calcValue === 'number' ? `${calcValue}px` : calcValue || null
}
</script>
<template>
  <component :is="tag" class="b-avatar-group" role="group">
    <div class="b-avatar-group-inner" :style="paddingStyle">
      <slot />
    </div>
  </component>
</template>

<script setup lang="ts">
import {computed, provide, readonly, type StyleValue, toRef} from 'vue'
import type {Booleanish, ColorVariant} from '../../types'
import {avatarGroupInjectionKey, isNumeric, toFloat} from '../../utils'
import {useBooleanish} from '../../composables'
import {computeSize} from './BAvatar.vue'

interface BAvatarGroupProps {
  overlap?: number | string
  rounded?: boolean | string
  size?: 'sm' | 'md' | 'lg' | string // size?: InputSize | string
  square?: Booleanish
  tag?: string
  variant?: ColorVariant
}

const props = withDefaults(defineProps<BAvatarGroupProps>(), {
  overlap: 0.3,
  rounded: false,
  square: false,
  tag: 'div',
  size: undefined,
  variant: undefined,
})

const squareBoolean = useBooleanish(toRef(props, 'square'))

const computedSize = computed<string | null>(() => computeSize(props.size))

const overlapScale = computed<number>(
  () => Math.min(Math.max(computeOverlap(props.overlap), 0), 1) / 2
)

const paddingStyle = computed<StyleValue>(() => {
  const value = computedSize.value ? `calc(${computedSize.value} * ${overlapScale.value})` : null
  return value ? {paddingLeft: value, paddingRight: value} : {}
})

const computeOverlap = (value: any): number =>
  typeof value === 'string' && isNumeric(value) ? toFloat(value, 0) : value || 0

provide(avatarGroupInjectionKey, {
  overlapScale,
  size: readonly(toRef(props, 'size')),
  square: squareBoolean,
  rounded: readonly(toRef(props, 'rounded')),
  variant: readonly(toRef(props, 'variant')),
})
</script>
<template>
  <component :is="computedTag" class="badge" :class="computedClasses" v-bind="computedLinkProps">
    <slot />
  </component>
</template>

<script lang="ts">
import {isLink, omit, pluckProps} from '../../utils'
import {useBooleanish} from '../../composables'
import {computed, defineComponent, type PropType, toRef} from 'vue'
import type {Booleanish, ColorVariant} from '../../types'
import BLink, {BLINK_PROPS} from '../BLink/BLink.vue'

const linkProps = omit(BLINK_PROPS, ['event', 'routerTag'] as const)

export default defineComponent({
  components: {BLink},
  props: {
    pill: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    tag: {type: String, default: 'span'},
    variant: {type: String as PropType<ColorVariant>, default: 'secondary'},
    textIndicator: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    dotIndicator: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    ...linkProps,
  },
  setup(props) {
    const pillBoolean = useBooleanish(toRef(props, 'pill'))
    const textIndicatorBoolean = useBooleanish(toRef(props, 'textIndicator'))
    const dotIndicatorBoolean = useBooleanish(toRef(props, 'dotIndicator'))
    const activeBoolean = useBooleanish(toRef(props, 'active'))
    const disabledBoolean = useBooleanish(toRef(props, 'disabled'))

    const computedLink = computed<boolean>(() => isLink(props))

    const computedTag = computed<string | typeof BLink>(() =>
      computedLink.value ? BLink : props.tag
    )

    const computedClasses = computed(() => [
      [`bg-${props.variant}`],
      {
        'active': activeBoolean.value,
        'disabled': disabledBoolean.value,
        'text-dark': ['warning', 'info', 'light'].includes(props.variant),
        'rounded-pill': pillBoolean.value,
        'position-absolute top-0 start-100 translate-middle':
          textIndicatorBoolean.value || dotIndicatorBoolean.value,
        'p-2 border border-light rounded-circle': dotIndicatorBoolean.value,
        'text-decoration-none': computedLink.value,
      },
    ])

    const computedLinkProps = computed(() =>
      computedLink.value ? pluckProps(props, linkProps) : {}
    )

    return {
      computedClasses,
      computedLinkProps,
      computedTag,
    }
  },
})
</script>
<template>
  <li class="breadcrumb-item" :class="computedClasses">
    <component
      :is="computedTag"
      :aria-current="computedAriaCurrent"
      v-bind="computedLinkProps"
      @click="clicked"
    >
      <slot>
        {{ text }}
      </slot>
    </component>
  </li>
</template>

<script lang="ts">
import {omit, pluckProps} from '../../utils'
import {useBooleanish} from '../../composables'
import {computed, defineComponent, type PropType, toRef} from 'vue'
import BLink, {BLINK_PROPS} from '../BLink/BLink.vue'
import type {Booleanish} from '../../types'

const linkProps = omit(BLINK_PROPS, ['event', 'routerTag'] as const)

export default defineComponent({
  components: {BLink},
  props: {
    ...linkProps,
    active: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    ariaCurrent: {type: String, default: 'location'},
    disabled: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    text: {type: String, default: undefined},
  },
  emits: ['click'],
  setup(props, {emit}) {
    const activeBoolean = useBooleanish(toRef(props, 'active'))
    const disabledBoolean = useBooleanish(toRef(props, 'disabled'))

    const computedClasses = computed(() => ({
      active: activeBoolean.value,
    }))

    const computedTag = computed<'span' | typeof BLink>(() =>
      activeBoolean.value ? 'span' : BLink
    )

    const computedAriaCurrent = computed(() =>
      activeBoolean.value ? props.ariaCurrent : undefined
    )

    const computedLinkProps = computed(() =>
      computedTag.value !== 'span' ? pluckProps(props, linkProps) : {}
    )

    const clicked = (e: MouseEvent): void => {
      if (disabledBoolean.value || activeBoolean.value) {
        e.preventDefault()
        e.stopImmediatePropagation()
        return
      }
      if (!disabledBoolean.value) emit('click', e)
    }

    return {
      computedLinkProps,
      computedClasses,
      computedTag,
      computedAriaCurrent,
      clicked,
    }
  },
})
</script>
