  modelValue: computed(() => modelValue.value),
  buttonVariant: readonly(toRef(props, 'buttonVariant')),
  form: readonly(toRef(props, 'form')),
  name: computedName,
  button: buttonsBoolean,
  state: stateBoolean,
  plain: plainBoolean,
  size: readonly(toRef(props, 'size')),
