  const transitionDelayMs = Number(transitionDelay.slice(0, -1)) * 1000
  const transitionDurationMs = Number(transitionDuration.slice(0, -1)) * 1000
  return transitionDelayMs + transitionDurationMs
}
type PropDefinition = {
  type: any[]
  default: any
}

export interface ComponentProps {
  [key: string]: PropDefinition
}

export default (
  prefix: string,
  breakpoints: string[],
  definition: PropDefinition
): ComponentProps =>
  breakpoints.concat(['sm', 'md', 'lg', 'xl', 'xxl']).reduce((props, breakpoint) => {
    props[
      !prefix ? breakpoint : `${prefix}${breakpoint.charAt(0).toUpperCase() + breakpoint.slice(1)}`
    ] = definition
    return props
  }, Object.create(null))
export interface ComponentProps {
  [key: string]: {type: any[]; default: any}
}

export default (
  props: Record<PropertyKey, unknown>,
  els: ComponentProps,
  propPrefix: string,
  classPrefix = propPrefix
): string[] =>
  Object.keys(els).reduce((arr: string[], prop) => {
    if (!props[prop]) return arr

    arr.push(
      [classPrefix, prop.replace(propPrefix, ''), props[prop]]
        .filter((e) => e && typeof e !== 'boolean')
        .join('-')
        .toLowerCase()
    )

    return arr
  }, [])
export default (suffix = ''): string =>
  `__BVID__${Math.random().toString().slice(2, 8)}___BV_${suffix}__`
import type {RouteLocationRaw} from 'vue-router'

export default (props: {href?: string; to?: RouteLocationRaw}): boolean =>
  !!(props.href || props.to)
import type {Slots, VNode} from 'vue'

export default (name: string, scope = {}, $slots: Slots = {}): VNode => {
  // Ensure names is an array
  const names = [name]
  let slot
  for (let i = 0; i < names.length && !slot; i++) {
    const name = names[i]
    slot = $slots[name]
  }
